-- إنشاء أسئلة متكاملة للبطاقة الأولى: مضيف (Host)
INSERT INTO grade11_game_questions (lesson_id, question_text, question_type, choices, correct_answer, difficulty_level, explanation, points) VALUES
-- أسئلة سهلة
('eadf3ef5-2bf4-4a45-8e91-ad009a948f27', 'ما هو المضيف في الشبكة؟', 'multiple_choice', '[{"id": "A", "text": "أي جهاز متصل بالشبكة"}, {"id": "B", "text": "فقط الخوادم"}, {"id": "C", "text": "فقط أجهزة الحاسوب"}, {"id": "D", "text": "فقط الطابعات"}]', 'A', 'easy', 'المضيف (Host) هو أي جهاز متصل بالشبكة سواء كان حاسوب، خادم، طابعة، هاتف ذكي، أو أي جهاز آخر', 10),

('eadf3ef5-2bf4-4a45-8e91-ad009a948f27', 'المضيف يحتاج عنوان IP للاتصال بالشبكة', 'true_false', '[{"id": "true", "text": "صحيح"}, {"id": "false", "text": "خطأ"}]', 'true', 'easy', 'كل مضيف في الشبكة يحتاج إلى عنوان IP فريد للاتصال والتواصل مع الأجهزة الأخرى', 10),

('eadf3ef5-2bf4-4a45-8e91-ad009a948f27', 'ما الذي يحتاجه المضيف للاتصال بالشبكة؟', 'multiple_choice', '[{"id": "A", "text": "عنوان IP"}, {"id": "B", "text": "كلمة مرور"}, {"id": "C", "text": "برنامج خاص"}, {"id": "D", "text": "إذن من المدير"}]', 'A', 'easy', 'العنوان IP هو المطلب الأساسي لأي مضيف للاتصال بالشبكة', 10),

-- أسئلة متوسطة
('eadf3ef5-2bf4-4a45-8e91-ad009a948f27', 'أي من التالي يعتبر مضيف في شبكة المكتب؟', 'multiple_choice', '[{"id": "A", "text": "جميع الأجهزة المذكورة"}, {"id": "B", "text": "الحاسوب الشخصي فقط"}, {"id": "C", "text": "الطابعة الشبكية فقط"}, {"id": "D", "text": "الخادم فقط"}]', 'A', 'medium', 'جميع الأجهزة المتصلة بالشبكة تعتبر مضيفين: الحاسوب، الطابعة، الخادم، الهاتف الذكي، إلخ', 15),

('eadf3ef5-2bf4-4a45-8e91-ad009a948f27', 'ما الفرق بين المضيف والخادم؟', 'multiple_choice', '[{"id": "A", "text": "الخادم نوع خاص من المضيفين"}, {"id": "B", "text": "لا يوجد فرق"}, {"id": "C", "text": "المضيف أقوى من الخادم"}, {"id": "D", "text": "الخادم لا يحتاج عنوان IP"}]', 'A', 'medium', 'الخادم (Server) هو نوع خاص من المضيفين يقدم خدمات للمضيفين الآخرين في الشبكة', 15),

-- أسئلة صعبة  
('eadf3ef5-2bf4-4a45-8e91-ad009a948f27', 'في شبكة تحتوي على 100 مضيف، كم عنوان IP فريد مطلوب؟', 'multiple_choice', '[{"id": "A", "text": "100 عنوان IP فريد"}, {"id": "B", "text": "50 عنوان IP"}, {"id": "C", "text": "200 عنوان IP"}, {"id": "D", "text": "عنوان IP واحد للجميع"}]', 'A', 'hard', 'كل مضيف يحتاج عنوان IP فريد ومتميز، لذلك 100 مضيف يحتاج 100 عنوان IP مختلف', 20);

-- إنشاء أسئلة للبطاقة الثانية: شبكة محلية LAN
INSERT INTO grade11_game_questions (lesson_id, question_text, question_type, choices, correct_answer, difficulty_level, explanation, points) VALUES
-- أسئلة سهلة
('17427e39-ad04-44b7-855c-347d9477a6b4', 'ما معنى LAN؟', 'multiple_choice', '[{"id": "A", "text": "شبكة محلية محدودة المنطقة"}, {"id": "B", "text": "شبكة واسعة النطاق"}, {"id": "C", "text": "شبكة الإنترنت"}, {"id": "D", "text": "شبكة لاسلكية"}]', 'A', 'easy', 'LAN تعني Local Area Network وهي شبكة محلية تغطي منطقة محدودة مثل المنزل أو المكتب', 10),

('17427e39-ad04-44b7-855c-347d9477a6b4', 'الشبكة المحلية LAN تغطي منطقة صغيرة', 'true_false', '[{"id": "true", "text": "صحيح"}, {"id": "false", "text": "خطأ"}]', 'true', 'easy', 'صحيح، الشبكة المحلية تغطي منطقة محدودة مثل منزل أو مبنى أو حرم جامعي', 10),

-- أسئلة متوسطة
('17427e39-ad04-44b7-855c-347d9477a6b4', 'أي من التالي يعتبر مثال على LAN؟', 'multiple_choice', '[{"id": "A", "text": "شبكة مكتب أو منزل"}, {"id": "B", "text": "الإنترنت العالمي"}, {"id": "C", "text": "شبكة بين المدن"}, {"id": "D", "text": "شبكة القمر الصناعي"}]', 'A', 'medium', 'شبكة المكتب أو المنزل مثال نموذجي على الشبكة المحلية LAN', 15),

('17427e39-ad04-44b7-855c-347d9477a6b4', 'ما مميزات الشبكة المحلية LAN؟', 'multiple_choice', '[{"id": "A", "text": "سرعة عالية وتكلفة منخفضة"}, {"id": "B", "text": "تغطية عالمية"}, {"id": "C", "text": "تعقيد في الإدارة"}, {"id": "D", "text": "بطء في النقل"}]', 'A', 'medium', 'الشبكة المحلية تتميز بسرعة عالية، تكلفة منخفضة، وسهولة الإدارة', 15),

-- أسئلة صعبة
('17427e39-ad04-44b7-855c-347d9477a6b4', 'ما أقصى مسافة تغطيها الشبكة المحلية عادة؟', 'multiple_choice', '[{"id": "A", "text": "بضعة كيلومترات"}, {"id": "B", "text": "آلاف الكيلومترات"}, {"id": "C", "text": "مئات الكيلومترات"}, {"id": "D", "text": "حول العالم"}]', 'A', 'hard', 'الشبكة المحلية تغطي عادة مسافة محدودة تصل لبضعة كيلومترات كحد أقصى', 20);

-- إنشاء أسئلة للبطاقة الثالثة: Hostname
INSERT INTO grade11_game_questions (lesson_id, question_text, question_type, choices, correct_answer, difficulty_level, explanation, points) VALUES
-- أسئلة سهلة
('aa5334b5-99fa-40f9-b821-0c4c5af07629', 'ما هو Hostname؟', 'multiple_choice', '[{"id": "A", "text": "اسم مفهوم للإنسان يمثل عنوان IP"}, {"id": "B", "text": "نوع من الكابلات"}, {"id": "C", "text": "برنامج حماية"}, {"id": "D", "text": "نوع من الفيروسات"}]', 'A', 'easy', 'Hostname هو اسم مفهوم للإنسان يمثل جهاز معين في الشبكة بدلاً من استخدام عنوان IP الرقمي', 10),

('aa5334b5-99fa-40f9-b821-0c4c5af07629', 'Hostname أسهل في التذكر من عنوان IP', 'true_false', '[{"id": "true", "text": "صحيح"}, {"id": "false", "text": "خطأ"}]', 'true', 'easy', 'صحيح، أسماء مثل www.google.com أسهل في التذكر من 172.217.16.14', 10),

-- أسئلة متوسطة  
('aa5334b5-99fa-40f9-b821-0c4c5af07629', 'ما الخدمة المسؤولة عن تحويل Hostname إلى IP؟', 'multiple_choice', '[{"id": "A", "text": "DNS (Domain Name System)"}, {"id": "B", "text": "DHCP"}, {"id": "C", "text": "HTTP"}, {"id": "D", "text": "FTP"}]', 'A', 'medium', 'DNS (Domain Name System) مسؤول عن تحويل أسماء النطاقات والمضيفين إلى عناوين IP', 15),

('aa5334b5-99fa-40f9-b821-0c4c5af07629', 'أي من التالي مثال صحيح على Hostname؟', 'multiple_choice', '[{"id": "A", "text": "server1.company.com"}, {"id": "B", "text": "192.168.1.1"}, {"id": "C", "text": "255.255.255.0"}, {"id": "D", "text": "00:1A:2B:3C:4D:5E"}]', 'A', 'medium', 'server1.company.com مثال على hostname، بينما الباقي عناوين رقمية (IP و MAC)', 15),

-- أسئلة صعبة
('aa5334b5-99fa-40f9-b821-0c4c5af07629', 'ما يحدث عند فشل خدمة DNS؟', 'multiple_choice', '[{"id": "A", "text": "لا يمكن الوصول للمواقع بالأسماء"}, {"id": "B", "text": "توقف الإنترنت تماماً"}, {"id": "C", "text": "تسريع التصفح"}, {"id": "D", "text": "تحسين الأمان"}]', 'A', 'hard', 'عند فشل DNS، يمكن الوصول للمواقع بعناوين IP المباشرة لكن ليس بالأسماء المألوفة', 20);

-- إنشاء أسئلة للبطاقة الرابعة: جدول MAC
INSERT INTO grade11_game_questions (lesson_id, question_text, question_type, choices, correct_answer, difficulty_level, explanation, points) VALUES
-- أسئلة سهلة
('7a3a5df4-4d07-46d1-8934-8cdefc9da5a1', 'ما هو جدول MAC؟', 'multiple_choice', '[{"id": "A", "text": "جدول يربط عناوين MAC بالمنافذ"}, {"id": "B", "text": "جدول كلمات المرور"}, {"id": "C", "text": "جدول المواقع المحجوبة"}, {"id": "D", "text": "جدول الأخطاء"}]', 'A', 'easy', 'جدول MAC يحتفظ بعناوين MAC للأجهزة ويربطها بالمنافذ التي متصلة بها في السويتش', 10),

('7a3a5df4-4d07-46d1-8934-8cdefc9da5a1', 'السويتش يستخدم جدول MAC لتوجيه الإطارات', 'true_false', '[{"id": "true", "text": "صحيح"}, {"id": "false", "text": "خطأ"}]', 'true', 'easy', 'صحيح، السويتش يستخدم جدول MAC لمعرفة أي منفذ يرسل إليه الإطار', 10),

-- أسئلة متوسطة
('7a3a5df4-4d07-46d1-8934-8cdefc9da5a1', 'كيف يتعلم السويتش عناوين MAC؟', 'multiple_choice', '[{"id": "A", "text": "من عنوان المصدر في الإطارات الواردة"}, {"id": "B", "text": "من الإعدادات اليدوية"}, {"id": "C", "text": "من الإنترنت"}, {"id": "D", "text": "من الطابعة"}]', 'A', 'medium', 'السويتش يتعلم عناوين MAC تلقائياً من عنوان المصدر في كل إطار يستقبله', 15),

('7a3a5df4-4d07-46d1-8934-8cdefc9da5a1', 'ماذا يحدث إذا امتلأ جدول MAC؟', 'multiple_choice', '[{"id": "A", "text": "يحذف أقدم المدخلات"}, {"id": "B", "text": "يتوقف السويتش"}, {"id": "C", "text": "يعيد تشغيل النظام"}, {"id": "D", "text": "يرسل تنبيه للمدير"}]', 'A', 'medium', 'عند امتلاء جدول MAC، يحذف السويتش أقدم المدخلات لإفساح المجال للعناوين الجديدة', 15),

-- أسئلة صعبة
('7a3a5df4-4d07-46d1-8934-8cdefc9da5a1', 'كم المدة الافتراضية لبقاء مدخل في جدول MAC؟', 'multiple_choice', '[{"id": "A", "text": "300 ثانية (5 دقائق)"}, {"id": "B", "text": "60 ثانية"}, {"id": "C", "text": "3600 ثانية"}, {"id": "D", "text": "لا ينتهي أبداً"}]', 'A', 'hard', 'المدة الافتراضية هي 300 ثانية، بعدها يحذف المدخل إذا لم يستخدم', 20);

-- إنشاء أسئلة للبطاقة الخامسة: تعريف Duplex والسرعات
INSERT INTO grade11_game_questions (lesson_id, question_text, question_type, choices, correct_answer, difficulty_level, explanation, points) VALUES
-- أسئلة سهلة
('843ba2d7-c41e-47af-ae2e-f7726fc24ceb', 'ما معنى Full Duplex؟', 'multiple_choice', '[{"id": "A", "text": "الإرسال والاستقبال في نفس الوقت"}, {"id": "B", "text": "الإرسال فقط"}, {"id": "C", "text": "الاستقبال فقط"}, {"id": "D", "text": "لا إرسال ولا استقبال"}]', 'A', 'easy', 'Full Duplex يعني القدرة على الإرسال والاستقبال في نفس الوقت دون تعارض', 10),

('843ba2d7-c41e-47af-ae2e-f7726fc24ceb', 'Half Duplex يسمح بالإرسال والاستقبال معاً', 'true_false', '[{"id": "false", "text": "خطأ"}, {"id": "true", "text": "صحيح"}]', 'false', 'easy', 'خطأ، Half Duplex يسمح بالإرسال أو الاستقبال لكن ليس في نفس الوقت', 10),

-- أسئلة متوسطة
('843ba2d7-c41e-47af-ae2e-f7726fc24ceb', 'أي سرعة شائعة في شبكات Ethernet الحديثة؟', 'multiple_choice', '[{"id": "A", "text": "1 Gbps"}, {"id": "B", "text": "56 Kbps"}, {"id": "C", "text": "1 Mbps"}, {"id": "D", "text": "128 Kbps"}]', 'A', 'medium', '1 Gbps (Gigabit per second) سرعة شائعة في شبكات Ethernet الحديثة', 15),

('843ba2d7-c41e-47af-ae2e-f7726fc24ceb', 'ما فائدة Auto-MDIX؟', 'multiple_choice', '[{"id": "A", "text": "يكتشف نوع الكابل تلقائياً"}, {"id": "B", "text": "يزيد السرعة"}, {"id": "C", "text": "يوفر الكهرباء"}, {"id": "D", "text": "يمنع الفيروسات"}]', 'A', 'medium', 'Auto-MDIX يكتشف تلقائياً نوع الكابل (مستقيم أو متقاطع) ويتكيف معه', 15),

-- أسئلة صعبة
('843ba2d7-c41e-47af-ae2e-f7726fc24ceb', 'في وضع Half Duplex، كيف يتم تجنب التصادمات؟', 'multiple_choice', '[{"id": "A", "text": "باستخدام CSMA/CD"}, {"id": "B", "text": "بزيادة السرعة"}, {"id": "C", "text": "بتقليل عدد الأجهزة"}, {"id": "D", "text": "بإيقاف الشبكة"}]', 'A', 'hard', 'CSMA/CD (Carrier Sense Multiple Access with Collision Detection) يستخدم لتجنب وكشف التصادمات', 20);